import Pagina from "../templates/Pagina";
import FormCadCategoriaProdutos from "../formularios/FormCadCategoriaProdutos";
import TabelaCategoriaProdutos from "../tabelas/tabelaCategoriaProdutos";
import { useState, useEffect } from "react";
import TelaCarregamento from "./TelaCarregamento";
import TelaErro from "./TelaErro";
import STATUS from "../Utilitarios/util";

export default function TelaCadastroCategoriaProdutos(props){

    function buscarCategoriaProdutos(){
        fetch('http://localhost:5000/categoriaprodutos',{method:"GET"})
        .then((resposta) => {
            return resposta.json();
        }).then((dados) => {
            setListaCategoriaProdutos(dados);
            setStatus(STATUS.carregado);      
        }).catch((erro) => {
            setStatus(STATUS.erro);
        });
            
    }
    
    const [exibirTabela, setExibirTabela] = useState(true);
    const [status, setStatus] = useState(STATUS.ocioso);
    const [listaCategoriaProdutos, setListaCategoriaProdutos] = useState([]);
    const [categoriaProdutoSelecionado, setCategoriaProdutoSelecionado] = useState({});
  
   
    useEffect(() => {
        setStatus(STATUS.ocioso);
        buscarCategoriaProdutos();
    },[])

  
    if(exibirTabela){

        if (status === STATUS.ocioso){
           return(
            <TelaCarregamento/>
                 );
    }
    else if (status === STATUS.carregado) {
        return(
            <Pagina>
                <TabelaCategoriaProdutos dados={listaCategoriaProdutos} onTabela={setExibirTabela}/>
            </Pagina>
        );
    }
    else{
        return(
            <TelaErro mensagem="Não foi possível recuperar os dados das Categorias de Produtos. 
            Por favor, entre em contato com a administração do sistema!"/>
        );
    }
}
else {
    return(
        <Pagina>
            <FormCadCategoriaProdutos onTabela={setExibirTabela} listaCategoriaProdutos={setListaCategoriaProdutos}/>
        </Pagina>
    );
}
    
}
