import { useState, useRef, useEffect } from "react";
import { Form, Row, Col, InputGroup, Button, Container } from "react-bootstrap";
import TelaCarregamento from "../TelasDeCadastro/TelaCarregamento.js";
import TelaErro from "../TelasDeCadastro/TelaErro.js";
import STATUS from "../Utilitarios/util.js";
import BarraBusca from "../componentes/busca/BarraBusca.jsx";
import CaixaSelecao from "../componentes/busca/CaixaSelecao.jsx";

export default function FormCadConsumo(props){
  //const [formValidado, setFormValidado] = useState(false);
  const [status, setStatus] = useState(STATUS.carregado);
  const [listaClientes,setListaClientes] = useState([]);
  const [clienteSelecionado, setClienteSelecionado] = useState({});
  //const [listaProdutos, setListaProdutos] = useState({});
  const [produtoSelecionado, setProdutoSelecionado] = useState({});
  const [validado, setvalidado] = useState(false);

  const[consumo, setConsumo] = useState({
    id: 0,
    datavenda: "",
    desconto: 0,
    totaltributos: 0,
    total: 0,
    cliente: {},
    listaProduto: []
  });

  useEffect(() => {
    fetch("http://localhost:5000/cliente", { method: "GET" })
    .then((resposta) => {
      return resposta.json();
    })
    .then((listaClientes) => {
      setStatus.carregado;
      setListaClientes(listaClientes);
      props.onTabela(true);
    })
    .catch(() => {
      //setStatus(STATUS.erro);
    });
  }, []);

  useEffect(() => {
    fetch("http://localhost:5000/produto", { method: "GET" })
      .then((resposta) => {
        return resposta.json();
      })
      .then((listadeProdutos) => {
        setProdutoSelecionado(listadeProdutos);
      })
      .catch((erro) => {
        console.log("Não foi possível recuperar dados do Produto no backend");
      });
  });

  function gravarVenda() {
    fetch("http://localhost:5000/venda", {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({
                        "datavenda": consumo.datavenda,
                        "desconto": consumo.desconto,
                        "valortributos": consumo.valortributos,
                        "total": consumo.total,
                        "cliente": {
                            "cpf": clienteSelecionado.cpf,
    }}),
  })
    .then((resposta) => {
      return resposta.json();
    })
    .then((dados) => {
      if(dados.status){
        setConsumo({...consumo, id:dados.id});
      }
      alert(dados.mensagem);
      props.buscaCliente.push(cliente);
      props.onTabela(true);
    })
    .catch((erro) => {
      //setStatus(STATUS.erro);
    });
  }

  function manipularMudanca(e) {
    const alvo = e.target.name;
    if(e.target.type === "checkbox"){
      setConsumo({ ...consumo, [alvo]: e.target.checked});
    }
    else{
      setConsumo({ ...consumo, [alvo]: e.target.value});
      //console.log("O usuário digitou: " + e.target.value);
    }
  }
    
  function manipulaSubmissao(evento) {
    const formulario = evento.currentTarget;
    if (formulario.checkValidity()) {

        //setStatus(STATUS.ocioso);
        setvalidado(true);
        gravarVenda();
        setStatus(STATUS.carregado);

    }
    evento.preventDefault();
    evento.stopPropagation();
    
  }
 
  

 if (status === STATUS.carregado) {
    return (
      <Container>
        <Row className="mt-3 mb-3 border d-flex text-center">
          <h2>Consumo de Produtos por Cliente</h2>
        </Row>
        <Row className="mt-3 p-2 border">
          <Row>
            <Form
              noValidate
              validated={validado}
              onSubmit={manipulaSubmissao}
            >
              <Row className="mb-3">
                <Row>
                  <Form.Group as={Col} md="2">
                    <Form.Label>ID</Form.Label>
                    <Form.Control
                      id="id"
                      name="id"
                      required
                      type="text"
                      placeholder=""
                      value={consumo.id}
                      onChange={manipularMudanca}
                    />
                    <Form.Control.Feedback type="invalid">
                      
                    </Form.Control.Feedback>
                  </Form.Group>

                  <Form.Group as={Col} md="4">
                  <InputGroup>
                    <Form.Group controlId="datavenda">
                      <Form.Label>Data da Venda</Form.Label>
                      <Form.Control type="date" name="datavenda" onChange={manipularMudanca} value={consumo.datavenda}/>
                    </Form.Group>

                    
                    <Form.Control.Feedback type="invalid">
                      Por favor, informe a data de consumo do cliente!
                    </Form.Control.Feedback>
                  </InputGroup>
                </Form.Group>
                </Row>                
                
              </Row>
              <Row className="mb-3">
                <Form.Group as={Col} md="3">
                  <Form.Label>Desconto(R$)</Form.Label>
                  <Form.Control
                    id="desconto"
                    name="desconto"
                    type="text"
                    placeholder=""
                    value={consumo.desconto}
                    onChange={manipularMudanca}
                  />
                  <Form.Control.Feedback type="invalid">

                  </Form.Control.Feedback>
                </Form.Group>
                <Form.Group as={Col} md="2">
                  <Form.Label>Total de Tributos</Form.Label>
                  <Form.Control
                    type="text"
                    id="valortributos"
                    name="valortributos"
                    placeholder=""
                    value={consumo.valortributos}
                    onChange={manipularMudanca}
                  />
                  <Form.Control.Feedback type="invalid">

                  </Form.Control.Feedback>
                </Form.Group>
                <Form.Group as={Col} md="2">
                  <Form.Label>Total</Form.Label>
                  <Form.Control
                    type="text"
                    id="total"
                    name="total"
                    placeholder=""
                    value={consumo.total}
                    onChange={manipularMudanca}
                  />
                  <Form.Control.Feedback type="invalid">
     
                  </Form.Control.Feedback>
                </Form.Group>
              </Row>
              <Row>
              <Form.Group as={Col} md="5">
                <Row className="mb-2">
                <Form.Label>Digite o nome do cliente</Form.Label>
                   <BarraBusca campoBusca={"nome"}
                               campoChave={"cpf"}
                               dados={listaClientes}
                               funcaoSelecao={setClienteSelecionado} 
                               placeHolder={"Digite o nome de um Cliente"}
                               valor=""
                    />
               </Row>
              </Form.Group>
              
              </Row>
              <Row>
                {
                  //Permite que os produtos sejam selecionados
                  //front-end de relacionamento muitos pra muitos
                  <container>                    
                    <Form.Group as={Col} md="3">
                      <Form.Label>Produto</Form.Label>
                       <CaixaSelecao
                       enderecoFonteDados="http://localhost:5000/produto"
                       campoChave={"codigo"}
                       campoExibicao={"nome"}
                       funcaoSelecao={setProdutoSelecionado}
                />
                    </Form.Group>
                  </container>

                }
              </Row> 
              <Row>
                <Col md={10}>
                  <Row>
                    <Col md={1} mt={5}>
                      <Form.Group>
                        <Form.Label>Código:</Form.Label>
                        <Form.Control type="text" value={produtoSelecionado?.id} disabled/>
                      </Form.Group>
                    </Col>
                    <Col md={6} mt={5}>
                      <Form.Group>
                        <Form.Label>Produto:</Form.Label>
                        <Form.Control type="text" value={produtoSelecionado?.nome} disabled/>
                      </Form.Group>
                    </Col>
                  </Row>
                </Col>
              </Row>
              <Row className="mt=4">
                <h3>Lista de Produtos Adicionados</h3>
              </Row>
              <Button type="submit">Cadastrar</Button>
             
            </Form>
          </Row>
        </Row>
      </Container>
    )
  } else if (status == STATUS.ocioso) {
    return <TelaCarregamento />;
  } else {
    return (
      <TelaErro mensagem="Não foi possível recuperar os dados das Vendas. Entre em contato com o administrador do sistema" />
    );
  
  }
}
