import Pagina from "../templates/Pagina";
import FormCadCamareiro from "../formularios/FormCadCamareiro";
import TabelaDeCamareiros from "../tabelas/tabelaDeCamareiro";
import { useState, useEffect } from "react";
import TelaCarregamento from "./TelaCarregamento";
import TelaErro from "./TelaErro";
import STATUS from "../Utilitarios/util";

export default function TelaCadastroDeCamareiro(props){

    function buscarCamareiros(){
        fetch('http://localhost:4000/camareiro',{method:"GET"})
        .then((resposta) => {
            return resposta.json();
        }).then((dados) => {
            setListaCamareiros(dados);
            setStatus(STATUS.carregado);      
        }).catch((erro) => {
            setStatus(STATUS.erro);
        });
            
    }
    
    const [exibirTabela, setExibirTabela] = useState(true);
    const [status, setStatus] = useState(STATUS.ocioso);
    const [listaCamareiros, setListaCamareiros] = useState([]);
    
    useEffect(() => {
        setStatus(STATUS.ocioso);
        buscarCamareiros();
    },[])

  
    if(exibirTabela){

        if (status === STATUS.ocioso){
           return(
            <TelaCarregamento/>
                 );
    }
    else if (status === STATUS.carregado) {
        return(
            <Pagina>
                <TabelaDeCamareiros dados={listaCamareiros} onTabela={setExibirTabela}/>
            </Pagina>
        );
    }
    else{
        return(
            <TelaErro mensagem="Não foi possível recuperar os dados dos camareiros. 
            Por favor, entre em contato com a administração do sistema!"/>
        );
    }
}
else {
    return(
        <Pagina>
            <FormCadCamareiro onTabela={setExibirTabela} listaCamareiros={listaCamareiros}/>
        </Pagina>
    );
}
    
}
