import { useState, useEffect } from "react";
import {Form, Row, Col, InputGroup, Button, Container} from "react-bootstrap";
import CaixaSelecao from "../componentes/busca/CaixaSelecao";


export default function FormCadCategoriaProdutos(props) {
  const [validacao, setValidacao] = useState(false);
  const [listaProdutos, setListaProdutos] = useState([]);
  const [produtoSelecionado, setProdutoSelecionado] = useState([]);
 

  const [produtos, setProdutos] = useState({
    codigo : 0,
    descricao : "",
    qtde:0,
    preco:0,
    categoria:""
  });

  useEffect(() => {
    fetch('http://localhost:5000/produto/', {method: "GET"})
    .then((resposta) => {
        return resposta.json();
    })
    .then((listadeProdutos)=>{
      setListaCategoriaProdutos(listadeProdutos);
    })
    .catch((erro) => {
        console.log("Não foi possível recuperar dados do Produto no backend");
    });
  },[]);

  function manipularMudanca(e) {
    const alvo = e.target.name;
    if(e.target.type === "checkbox"){
      produtos({...listaProdutos, [alvo]: e.target.checked});
    }
    else{
      produtos({...listaProdutos, [alvo]: e.target.value});
      console.log("O usuário digitou: " + e.target.value);
    }
  }

  function gravarProdutos(){
    fetch('http://localhost:5000/produto/', {
      method: "POST",
      headers: {"Content-Type": "application/json"},
      body: JSON.stringify({         
        "codigo": produtos.codigo,
        "descricao": produtos.descricao, 
        "preco": produtos.qtde, 
        "preco": produtos.preco, 
        "categoria": produtos.categoria
      })

    })
    .then((resposta) => {
        return resposta.json();
    })
    .then((listadeProdutos)=>{
      setListaProdutos(listadeProdutos);
    })
    .catch((erro) => {
        erro.message("Não foi possível gravar o Produto");
    });
  }

  const manipulaSubmissao = (event) => {
    const form = event.currentTarget;
    if (form.checkValidity() === false) {
       setValidacao(false);
       gravarProdutos();
    }
    else{
       setValidacao(true);
    }
    event.preventDefault();
    event.stopPropagation();

  };

  return (
    <Container>
      <Row className="mt-3 mb-3 border d-flex text-center">
        <h2>Cadastro de Produtos</h2>
      </Row>
      <Row>
        <Form noValidate validated={validacao} onSubmit={manipulaSubmissao}>
          <Form.Group as={Col} md="1">
              <Form.Label>descricao</Form.Label>
              <InputGroup hasValidation>
                <Form.Control
                  required
                  type="text"
                  placeholder=""
                  id="descricao"
                  name="descricao"   
                  value = {produtos.descricao}  
                  onChange={manipularMudanca}                               
                />
                <Form.Control.Feedback type="invalid">
                   Informe a descrição do Produto!
                </Form.Control.Feedback>
              </InputGroup>
          </Form.Group> 
          <Form.Group as={Col} md="1">
              <Form.Label>Quantidade</Form.Label>
              <InputGroup hasValidation>
                <Form.Control
                  required
                  type="text"
                  placeholder=""
                  id="qtde"
                  name="qtde"   
                  value = {produtos.descricao}  
                  onChange={manipularMudanca}                               
                />
                <Form.Control.Feedback type="invalid">
                   Informe a quantidade do produto!
                </Form.Control.Feedback>
              </InputGroup>
          </Form.Group>
          <Form.Group as={Col} md="1">
              <Form.Label>Preço</Form.Label>
              <InputGroup hasValidation>
                <Form.Control
                  required
                  type="text"
                  placeholder=""
                  id="preco"
                  name="preco"   
                  value = {produtos.preco}  
                  onChange={manipularMudanca}                               
                />
                <Form.Control.Feedback type="invalid">
                   Informe o preço do produto!
                </Form.Control.Feedback>
              </InputGroup>
          </Form.Group> 
          <Form.Group as={Col} md="3">
          <Form.Label>Categoria</Form.Label>
             <CaixaSelecao enderecoFonteDados={listaProdutos} 
                           campoChave="codigo"
                           campoExibicao="title"
                           funcaoSelecao={setProdutoSelecionado}
             />
          </Form.Group>                  
          
          <Button type="submit">Cadastrar</Button>
          <Button
            type="button"
            onClick={() => {
              
            }}
          >
            Voltar
          </Button>
        </Form>
      </Row>
    </Container>
  );
}
