import React, { useState, useRef } from 'react';
import { Container } from 'react-bootstrap';
import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import Row from 'react-bootstrap/Row';
import TelaCarregamento from '../TelasDeCadastro/TelaCarregamento';
import STATUS from '../Utilitarios/util';
import TelaErro from '../TelasDeCadastro/TelaErro';

export default function FormCadAtividadeCamareiro(props){

      function cadastrarAtividadeCamareiro(atividadecamareiro){
        fetch('http://localhost:4000/atividadecamareiro', {
          method:"POST",
          headers:{"Content-Type":"application/json"},
          body: JSON.stringify(atividadecamareiro)
        }).then((resposta) => {
          return resposta.json();
        }).then((dados) => {  
            setStatus(STATUS.carregado);
            alert(dados.mensagem);
            props.listaAtividadesCamareiro.push(atividadecamareiro);
            props.onTabela(true);          
        }).catch((erro) => {
            setStatus(STATUS.erro);
            console.log(erro.message);
        });
      }
    
        const [formValidado, setformValidado] = useState(false);
        const [status, setStatus] = useState(STATUS.carregado);
        const id = useRef("");
        const descricao = useRef("");
        const prioridade = useRef("");
        const tempoMedioDuracaoMin = useRef("");
        
        function validarDados(){
          const atividadecamareiro = {
            id: id.current.value,
            descricao: descricao.current.value,
            prioridade: prioridade.current.value,
            tempoMedioDuracaoMin: tempoMedioDuracaoMin.current.value
          }
          if (atividadecamareiro.id && atividadecamareiro.descricao && atividadecamareiro.prioridade && 
            atividadecamareiro.tempoMedioDuracaoMin)
            return atividadecamareiro;
          else
            return undefined;
        }
       
      
        function manipularSubmissao(evento){
          const formulario = evento.currentTarget;
          if (formulario.checkValidity()) {
            const atividadecamareiro = validarDados();
            if (atividadecamareiro){
                setStatus(STATUS.ocioso);
                cadastrarAtividadeCamareiro(atividadecamareiro);
                //props.listaAtividadeCamareiro.push(atividadecamareiro); gambiarra não
               }
          }
          evento.preventDefault();
          evento.stopPropagation();
      
          setformValidado(true);
        }

        if (status == STATUS.carregado) {      
        return (
        <Container>
            <Row className="mt-3 mb-3 border d-flex text-center">
                <h2>Atividades e serviços dos camareicos</h2>
            </Row>
            <Row>
            <Form noValidate validated={formValidado} onSubmit={manipularSubmissao}>
            <Row className="mb-3">
              <Form.Group as={Col} md="4">
                <Form.Label>id</Form.Label>
                <Form.Control
                  id="id"
                  name="id"
                  required
                  type="number"
                  placeholder="Id da atividade"
                  ref={id}
                />
                <Form.Control.Feedback type="invalid">
                Informe o ID da atividade!
                </Form.Control.Feedback>
              </Form.Group>
              <Form.Group as={Col} md="6">
                <Form.Label>Descrição</Form.Label>
                <InputGroup hasValidation>
                <Form.Control
                  required
                  type="text"
                  placeholder="Descrição da atividade"
                  id="descricao"
                  name="descricao"
                  ref={descricao}
                />
                <Form.Control.Feedback type="invalid">
                Informe a descrição da atividade!
                </Form.Control.Feedback>
                </InputGroup>
              </Form.Group>
            </Row>
            <Row className="mb-3">
              <Form.Group as={Col} md="3">
                <Form.Label>Prioridade</Form.Label>
              <Form.Select aria-label="Default select example" id="prioridade" 
                name="prioridade"
                required
                ref={prioridade} >
                       <option value="BAIXA">1-Baixa</option>
                       <option value="MÉDIA">2-Média</option>
                       <option value="ALTA">3-Alta</option>
                       <option value="URGENTE">4-Urgente</option>
                </Form.Select>
                <Form.Control.Feedback type="invalid">
                  Informe a prioridade!
                </Form.Control.Feedback>
              </Form.Group>
              <Form.Group as={Col} md="4">
                <Form.Label>Tempo Médio de duração em minutos</Form.Label>
                <InputGroup hasValidation>
                <Form.Control type="text" 
                placeholder=" " 
                id="tempoMedioDuracaoMin" 
                name="tempoMedioDuracaoMin"
                required
                ref={tempoMedioDuracaoMin} />
                <Form.Control.Feedback type="invalid">
                  Por favor, informe o tempo médio de duração!
                </Form.Control.Feedback>
                </InputGroup>
                </Form.Group>
                </Row>
                <Button type="submit">Cadastrar atividade</Button>
                <Button type="button" onClick={ ()=>{
                  props.onTabela(true);
                }}>Voltar</Button>
                
          </Form>
        </Row>
    </Container>
        );
     }else if (status == STATUS.ocioso){
      return (
        <TelaCarregamento/>
      );
     }
     else{
      return(
        <TelaErro mensagem="Não foi possível gravar a atividade. 
        Por favor, entre em contato com a administração do sistema!"/>
      );
     }
 }
