import Pagina from "../templates/Pagina";
import FormCadAtividadeCamareiro from "../formularios/FormCadAtividadeCamareiro";
import TabelaDeAtividadeCamareiro from "../tabelas/tabelaDeAtividadeCamareiro";
import { useState, useEffect } from "react";
import TelaCarregamento from "./TelaCarregamento";
import TelaErro from "./TelaErro";
import STATUS from "../Utilitarios/util";

export default function TelaCadastroDeAtividadeCamareiro(props){

    function buscarAtividadeCamareiro(){
        fetch('http://localhost:4000/atividadecamareiro',{method:"GET"})
        .then((resposta) => {
            return resposta.json();
        }).then((dados) => {
            setListaAtividadeCamareiro(dados);
            setStatus(STATUS.carregado);      
        }).catch((erro) => {
            setStatus(STATUS.erro);
        });
            
    }
    
    const [exibirTabela, setExibirTabela] = useState(true);
    const [status, setStatus] = useState(STATUS.ocioso);
    const [listaAtividadeCamareiro, setListaAtividadeCamareiro] = useState([]);
    
    useEffect(() => {
        setStatus(STATUS.ocioso);
        buscarAtividadeCamareiro();
    },[])

  
    if(exibirTabela){

        if (status === STATUS.ocioso){
           return(
            <TelaCarregamento/>
                 );
    }
    else if (status === STATUS.carregado) {
        return(
            <Pagina>
                <TabelaDeAtividadeCamareiro dados={listaAtividadeCamareiro} onTabela={setExibirTabela}/>
            </Pagina>
        );
    }
    else{
        return(
            <TelaErro mensagem="Não foi possível recuperar os dados das atividades dos camareiros. 
            Por favor, entre em contato com a administração do sistema!"/>
        );
    }
}
else {
    return(
        <Pagina>
            <FormCadAtividadeCamareiro onTabela={setExibirTabela} listaAtividadeCamareiro={listaAtividadeCamareiro}/>
        </Pagina>
    );
}
    
}
