import Pagina from "../templates/Pagina";
import FormCadProdutos from "../formularios/FormCadProdutos";
import TabelaProdutos from "../tabelas/tabelaProdutos";
import { useState, useEffect } from "react";
import TelaCarregamento from "./TelaCarregamento";
import TelaErro from "./TelaErro";
import STATUS from "../Utilitarios/util";

export default function TelaCadastroProdutos(props){

    function buscarProdutos(){
        fetch('http://localhost:5000/produto',{method:"GET"})
        .then((resposta) => {
            return resposta.json();
        }).then((dados) => {
            setListaProdutos(dados);
            setStatus(STATUS.carregado);      
        }).catch((erro) => {
            setStatus(STATUS.erro);
        });
            
    }
    
    const [exibirTabela, setExibirTabela] = useState(true);
    const [status, setStatus] = useState(STATUS.ocioso);
    const [listaProdutos, setListaProdutos] = useState([]);
    const [produtoSelecionado, setProdutoSelecionado] = useState({});
  
   
    useEffect(() => {
        setStatus(STATUS.ocioso);
        buscarProdutos();
    },[])

  
    if(exibirTabela){

        if (status === STATUS.ocioso){
           return(
            <TelaCarregamento/>
                 );
    }
    else if (status === STATUS.carregado) {
        return(
            <Pagina>
                <TabelaProdutos dados={listaProdutos} onTabela={setExibirTabela}/>
            </Pagina>
        );
    }
    else{
        return(
            <TelaErro mensagem="Não foi possível recuperar os dados das Categorias de Produtos. 
            Por favor, entre em contato com a administração do sistema!"/>
        );
    }
}
else {
    return(
        <Pagina>
            <FormCadProdutos onTabela={setExibirTabela} listaProdutos={setListaProdutos} atualizando={atualizando} produto={produtoEmEdicao}/>
        </Pagina>
    );
}
    
}
